package controllers

import play.api._
import play.api.mvc._
import model.{Profile, Gender}
import scala.concurrent.Future, Future._
import java.io.{File, ByteArrayInputStream}
import scala.annotation.tailrec
import play.api.libs.ws.WS
import play.api.libs.json.Json

object Application extends Controller {

  import play.api.libs.concurrent.Execution.Implicits._

  @tailrec
  private def messages(e: Throwable, prev: List[String] = Nil): List[String] = {
    val next = prev :+ e.getMessage
    if (e.getCause == null) next
    else messages(e.getCause, next)
  }

  private def guessr(raw: String): Future[Map[String, String]] = {
    import scala.sys.process._
    import play.api.Play.current

    val cmd = Play.configuration.getString("guess.command").getOrElse("./guess.sh")
    val cwd = Play.configuration.getString("guess.cwd").getOrElse(".")

    Future {
      try {
        val input = new ByteArrayInputStream(raw.getBytes)
        val p = (Process(cmd, Some(new File(cwd))) #< input)
        val output = (p !!) split ("\n") map {
          line ⇒
            line.split("=") match {
              case Array(key, value) ⇒ key → value
            }
        }
        output.toMap
      }
    }
  }

  def guess = Action(BodyParsers.parse.tolerantText) { request ⇒
    Async {
      (for {
        guess ← guessr(request.body)
      } yield Ok(views.html.guess(guess("age").toInt, Gender.withName(guess("gender"))))) recover {
        case e: Exception ⇒ Ok(messages(e).mkString("\n"))
      }
    }
  }

  def index = Action { implicit request ⇒
    Ok(views.html.index(routes.Application.token().absoluteURL()))
  }

  def token = Action(BodyParsers.parse.urlFormEncoded) { implicit request ⇒
    Async {
      val token = request.body("token").head
      WS.url("https://rpxnow.com/api/v2/auth_info").post(Map(
        "apiKey" -> Seq("a73b1ebad94f0f650c48d9c7dbc341375d22c7b5"),
        "token" -> Seq(token)
      )) map { response =>
        println(response.json)
        Redirect("/").withCookies(Cookie("token", token))
      }
    }
  }


  def index2 = Action { implicit request ⇒
    import Json._
    import play.api.Play.current

    implicit val profilesFormat = format[model.Profile]

